interface AEPSBank {
    bankId: string;        // Bank ID
    bankName: string;      // Full name of the bank (e.g., 'STATE BANK OF INDIA')
    bankShortName: string; // Short name or abbreviation of the bank (e.g., 'SBI')
    neft: string;          // NEFT support (1 = supported, 0 = not supported)
    rtgs: string;          // RTGS support (1 = supported, 0 = not supported)
    imps: string;          // IMPS support (1 = supported, 0 = not supported)
    aeps: string;          // AEPS support (1 = supported, 0 = not supported)
  }
  
export interface AEPSBankListResponse {
    orderId: string;        // Order ID generated by the API
    status: string;         // Status of the request (e.g., 'SUCCESS')
    bankList: AEPSBank[];   // List of AEPS supported banks
    resText: string;        // Response text (e.g., 'AEPS supported banks fetched successfully')
    resCode: string;        // Response code (e.g., '200')
  }