export interface OutletStatusRequestModel {
    outletMobile: string;
    aadhaarNumber: string;
}

interface OutletStatusData {
    status: string;  // Status of the outlet (e.g., 'SUCCESS', 'FAILED')
  }
  
export interface OutletStatusResponse {
    orderId: string;        // Order ID generated by the API
    status: string;         // Status of the request (e.g., 'SUCCESS')
    outletData: OutletStatusData; // Outlet-related status data
    resText: string;        // Response text (e.g., 'Outlet status fetched')
    pointUsed: string;      // Points used in the transaction (if applicable)
    resCode: string;        // Response code (e.g., '200')
  }
  