interface OutletData {
    otpStatus: string;  // OTP status for the outlet registration (e.g., 'SUCCESS')
  }
  
export interface RegisterOutletResponseData {
    orderId: string;           // Order ID generated by the API
    status: string;            // Status of the transaction (e.g., 'SUCCESS', 'FAILURE')
    outletData: OutletData;    // Data related to the outlet (OTP status)
    resText: string;           // Response text indicating further info (e.g., 'Outlet sent to mobile')
    pointUsed: string;         // Points used in the transaction (if applicable)
    resCode: string;           // Response code (if applicable)
}
  